INSERT INTO citys.alllines (id, dest_id, dest_name, dest_en_name, navigation_bar, real_city, city, special_remind, photos, score, ranking, recommend_playtime, comment_num, lat, lng, percent, travelbook_num, summary_description, address, phone, website, opening_hours, admission_ticket, tourist_season, traffic_guide, tips, grade, time_reference, url, year, old_1, old_2, old_3, old_4, fare_1, fare_2, fare_3, fare_4, fare_5, fare_6, fare_7, fare_8, fare_9, fare_10, fare_11, fare_12, fare_total, provinceName) VALUES (310, '10094607', '漫园', null, '旅行  > 目的地 > 中国 > 安徽 > 铜陵县 > 铜陵县景点 > 漫园', '铜陵', '铜陵县', null, 'https://img1.qunarzz.com/travel/poi/1712/54/75814be0460dbc37.jpg', '19', '铜陵县景点排名第1', null, '1', '31.0379617446', '117.8977006356', '0% 去过铜陵县的驴友来过这里', '0篇 旅行攻略中提到了这里', '位于铜陵市义安区（原铜陵县）西联乡犁桥村的“漫园”，虽然远离皖南的徽州地带，但其小桥流水、亭台楼阁、曲经廊檐、徽州物件处处皆风情……，如若步进徽州当地，千年徽州聚漫园，一园藏尽古今情，令人流连忘返。
漫园由3栋古民居和8个院落组成，虽经历岁月沧桑仍然完整的保存着徽州原木文化的风貌和格局，全园以木为本，深宅大院，重脊高檐，穿竹石栏，临河水阁，呈现一派古朴、明洁的幽静；古旧木屋瓦舍，还有清清湖水的气息，是典型的徽州文化景观建筑。', '铜陵市义安区西联镇', '181****9027', null, '8:30-17:30', '50元', null, null, null, null, null, 'http://travel.qunar.com/p-oi10094607-manyuan', '2019', '82', '59', '177', '314', '12', '32', '76', '72', '113', '158', '121', '36', '388', '169', '586', '459', '2222', null);
