INSERT INTO citys.alllines (id, dest_id, dest_name, dest_en_name, navigation_bar, real_city, city, special_remind, photos, score, ranking, recommend_playtime, comment_num, lat, lng, percent, travelbook_num, summary_description, address, phone, website, opening_hours, admission_ticket, tourist_season, traffic_guide, tips, grade, time_reference, url, year, old_1, old_2, old_3, old_4, fare_1, fare_2, fare_3, fare_4, fare_5, fare_6, fare_7, fare_8, fare_9, fare_10, fare_11, fare_12, fare_total, provinceName) VALUES (357, '710129', '四明山国家森林公园', null, '旅行  > 目的地 > 中国 > 浙江 > 四明山 > 四明山景点 > 四明山国家森林公园', '四明山', '四明山', '2018年3月24日—至5月31日四明山举办“花开四明”—2018余姚四明山旅游节。红枫、樱花、油菜花竞相争艳，详情请点击查看。', 'https://img1.qunarzz.com/travel/d5/1604/2f/1ae0f09c00be4f7.jpg', '14', '四明山景点排名第1', '4小时', '17', '29.742041', '121.126022', '1% 去过四明山的驴友来过这里', '2篇 旅行攻略中提到了这里', '四明山森林公园位于浙东四明腹地的余姚四明山镇，为省级森林公园。公园占地面积6.9万亩，林区内古木参天，千峰竞翠，湖泊连绵，奇岩众多，生态旅游独呈风采。
主要景点有：以中国水帘洞之最著称的鹁鸪岩，有山中明珠--仰天湖、东谷湖和下坑湖，有三面环壁、泉水叮咚的白龙潭，有横贯深崖中间的石梁--仙人桥，还有第二庐山之称的商量岗等。商量岗主峰奶部山海拔915米，是全市海拔最高点，登临顶峰，纵目四望，林海茫茫，松涛万顷。
在炎热的夏季，这里是人们避暑的好去处，可与江西庐山相媲美。1935年，蒋介石返乡曾与夫人宋美龄在这', '浙江省宁波市余姚市四明路四明山镇甘竹岭', '0574-62340889', null, '8:00-17:00', '票价
成人票50元；
优惠政策
1、1.2米-1.5米的儿童可享半价；60-70岁的老人持相关证件可享老人票。
2、1.2米以下的儿童、70岁以上的老人免票；', '四季皆宜。', '汽车
1、宁波汽车南站13:35分有一班中巴车直达四明山镇（2个小时），再转到四明山国家森林公园。
2、宁波汽车南站上午7:00左右赶到奉化江口，乘坐奉化到余姚的中巴到四明山镇。
3、从余姚走，宁波汽车中巴南站坐余姚中巴车到余姚南站（1个半小时），然后在余姚南站转乘四明山方向的中巴车即可（约2个小时），余姚南站到四明山的始发时间是6:40，末班是下午14:30。', null, null, null, 'http://travel.qunar.com/p-oi710129-simingshanguojiasenlin', '2023', '43', '59', '289', '288', '13', '88', '40', '31', '88', '72', '37', '105', '286', '97', '42', '277', '1176', null);
