INSERT INTO citys.alllines (id, dest_id, dest_name, dest_en_name, navigation_bar, real_city, city, special_remind, photos, score, ranking, recommend_playtime, comment_num, lat, lng, percent, travelbook_num, summary_description, address, phone, website, opening_hours, admission_ticket, tourist_season, traffic_guide, tips, grade, time_reference, url, year, old_1, old_2, old_3, old_4, fare_1, fare_2, fare_3, fare_4, fare_5, fare_6, fare_7, fare_8, fare_9, fare_10, fare_11, fare_12, fare_total, provinceName) VALUES (361, '707608', '西山风景区', null, '旅行  > 目的地 > 中国 > 江苏 > 苏州 > 苏州景点 > 西山风景区', '苏州', '苏州', null, 'https://img1.qunarzz.com/travel/poi/1803/6c/1fa3f48a8119c237.jpg', '38', '苏州景点排名第28', '3天 - 6天', '5', '31.129671', '120.307859', '0% 去过苏州的驴友来过这里', '17篇 旅行攻略中提到了这里', '西山是洞庭山的简称，南北宽11公里，东西长15公里，面积79.8平方公里，系太湖第一大岛。西山景区是太湖风景名胜区的精华。它是以群岛风光、花果丛林、吴越以来的古迹见长，以浏览、度假为主的湖岛区。它拥有湖中群岛、湖湾山水、山中坞谷、山顶峰峦四个风景层次。全区规划建设缥缈云场、水月问茶、林屋晚烟、消夏渔歌、里犁云、玄阳稻浪、肖山遗踪、毛公积雪、西湖夕照、石 公秋月，风凰烟雨、金铎松篁、文化巽峰、大沙观帆、罗汉古刹、鸡笼梅雪、明湾古村、角风涛、天王鱼国、横山旭日等二十个景点。', '苏州市吴中区金庭镇', '0512-66310058、0512-66310038、4000688798', 'http://www.xsly.org/', '西山全天开放，内部各小景点开放时间不一，大致为：夏季8:00-17:00，冬季8:00-16:30。', '无需门票。西山无需门票，内部各小景点门票价格不一。林屋洞50元，石公山50元，明月湾古村50元，缥缈峰60元。更多景点门票详见官网：http://www.xslyw.org/h-pd-18-101_330.html。', null, '当地交通
镇内旅游旺季时有小中巴往返于各景点，每人每站2元，平时可租乘机动三轮车或微型面包车前往，起步费5元。

地铁
乘坐地铁1号线在【木渎站】下，换乘69路，69路走西山岛的东路。

公交
金庭换乘枢纽站可坐694路、695路、697路，线路基本涵盖了岛上各个景点。', null, null, null, 'http://travel.qunar.com/p-oi707608-xishanfengjingqu', '2019', '99', '48', '156', '74', '80', '63', '22', '57', '31', '92', '42', '47', '472', '170', '204', '173', '1453', null);
INSERT INTO citys.alllines (id, dest_id, dest_name, dest_en_name, navigation_bar, real_city, city, special_remind, photos, score, ranking, recommend_playtime, comment_num, lat, lng, percent, travelbook_num, summary_description, address, phone, website, opening_hours, admission_ticket, tourist_season, traffic_guide, tips, grade, time_reference, url, year, old_1, old_2, old_3, old_4, fare_1, fare_2, fare_3, fare_4, fare_5, fare_6, fare_7, fare_8, fare_9, fare_10, fare_11, fare_12, fare_total, provinceName) VALUES (362, '712301', '马蹄寺', 'Matisi Grottoes', '旅行  > 目的地 > 中国 > 甘肃 > 张掖 > 张掖景点 > 马蹄寺', '张掖', '张掖', null, 'https://img1.qunarzz.com/travel/d3/1411/57/57fc9027bc826a5d213a9cdb.jpg', '22', '张掖景点排名第4', '3小时', '139', '38.509348', '100.43466', '24% 去过张掖的驴友来过这里', '163篇 旅行攻略中提到了这里', '马蹄寺位于肃南裕固族自治县境内，北距张掖市65公里，是集石窟艺术、祁连山风光和裕固族风情于一体的旅游区。石窟由胜果寺、普光寺、千佛洞、金塔寺、上、中、下观音洞七处组成，共有70余处窟龛，始建于北凉。马蹄寺因传说中的天马在此落有马蹄印而得名。传说中的马蹄印迹现存于普光寺马蹄殿内，成为镇寺之宝。马蹄寺石窟的独特之处在于千佛洞有500多个摩崖佛塔窟龛，规模宠大；金塔寺中的大型肉雕飞天古朴稚雅，为国内仅有；普光寺的三十三天洞，上下五层二十一窟，宝塔形排列，内有佛殿，外有回廊，共开内外窟龛达49孔之多，造型奇特。马', '位于肃南裕固族自治县境内', '0936-8891610', 'http://www.mtsly.cn/', '8:30-18:00', '旺季44元，淡季35元', '游览最好选择在秋季，路边的景色变化大，石窟也赋予了特殊的活力，夏天也可以，但是避开雨天。春天风沙大，看石窟不太适合。', '汽车南站有直达景区的一日游班车，往返21元每人，人少会停开。也可以乘坐往南古方向的车，在马蹄河下，往返19元每人，然后打车上山，一般到景区停车场要20-30元，如果从市区包车前往大概要120元。', null, 'AAAA', '2-3小时', 'http://travel.qunar.com/p-oi712301-matisi', '2023', '40', '30', '165', '82', '40', '23', '37', '39', '52', '118', '36', '48', '296', '112', '317', '83', '1201', null);
