INSERT INTO citys.alllines (id, dest_id, dest_name, dest_en_name, navigation_bar, real_city, city, special_remind, photos, score, ranking, recommend_playtime, comment_num, lat, lng, percent, travelbook_num, summary_description, address, phone, website, opening_hours, admission_ticket, tourist_season, traffic_guide, tips, grade, time_reference, url, year, old_1, old_2, old_3, old_4, fare_1, fare_2, fare_3, fare_4, fare_5, fare_6, fare_7, fare_8, fare_9, fare_10, fare_11, fare_12, fare_total, provinceName) VALUES (501, '10001594', '茉莉轩', null, '旅行  > 目的地 > 中国 > 海南 > 临高 > 临高景点 > 茉莉轩', '临高', '临高', null, 'https://img1.qunarzz.com/travel/poi/1712/20/462eef023ae56b37.jpg', '46', '临高景点排名第7', '2小时', '0', '19.9183120728', '109.6997139824', '0% 去过临高的驴友来过这里', '0篇 旅行攻略中提到了这里', '位于临城镇西北4公里处，在现在的临高师范学校内。茉莉轩原为宋代临高县令谢渥所建的学馆。谢渥在庭院内种了许多茉莉花，故名“茉莉轩”。宋绍兴十八年（公元1146年），宰相胡铨被贬到海南来，他经过临高时，谢渥迎接他到茉莉轩中住了一段时间。胡铨在此写下了一首《题茉莉轩》的诗，从此茉莉轩名声越来越大。
据临高县志记载，当时临高县博顿村有个叫戴定实的人曾得到胡铨的指点，因而发奋读书，后来考中举人并在吏部任职。历来人们都把茉莉轩看作是中原文化在临高传播的一个中心。', '临城镇西北4公里处，临高师范学校内。', null, null, null, null, null, null, null, null, null, 'http://travel.qunar.com/p-oi10001594-molixuan', '2019', '79', '68', '291', '264', '72', '42', '18', '37', '39', '168', '98', '195', '332', '232', '195', '459', '1887', null);
